/// <reference types="node" />
/// <reference types="react" />
/// <reference types="react-dom" />

declare namespace NodeJS {
  interface ProcessEnv {
    readonly NODE_ENV: 'development' | 'production' | 'test';
    readonly PUBLIC_URL: string;

    readonly REACT_IS_MAINNET: boolean;
    readonly REACT_APP_API_URL: string;
    readonly REACT_INFURA_KEY: string;

    readonly REACT_ZEROGAS_ADDRESS_TESTNET: string;
    readonly REACT_CROWDSALE_ADDRESS_TESTNET: string;

    readonly REACT_ZEROGAS_ADDRESS_MAINNET: string;
    readonly REACT_CROWDSALE_ADDRESS_MAINNET: string;
  }
}

declare module '*.avif' {
  const src: string;
  export default src;
}

declare module '*.bmp' {
  const src: string;
  export default src;
}

declare module '*.gif' {
  const src: string;
  export default src;
}

declare module '*.jpg' {
  const src: string;
  export default src;
}

declare module '*.jpeg' {
  const src: string;
  export default src;
}

declare module '*.png' {
  const src: string;
  export default src;
}

declare module '*.webp' {
  const src: string;
  export default src;
}

declare module '*.svg' {
  import * as React from 'react';

  export const ReactComponent: React.FunctionComponent<
    React.SVGProps<SVGSVGElement> & { title?: string }
  >;

  const src: string;
  export default src;
}

declare module '*.module.css' {
  const classes: { readonly [key: string]: string };
  export default classes;
}

declare module '*.module.scss' {
  const classes: { readonly [key: string]: string };
  export default classes;
}

declare module '*.module.sass' {
  const classes: { readonly [key: string]: string };
  export default classes;
}

declare module 'eruda' {
  export function init();
  export function add(module: any);
}

declare module 'eruda-code';
declare module 'eruda-dom';
